INSERT INTO faqs (question, answer, display_order) VALUES
('TODER''e kimler üye olabilir?', 'Otopark işletmeciliği, otopark teknolojileri entegratörlüğü veya otopark yatırımı alanlarında faaliyet gösteren tüzel kişilikler ve şahıs şirketleri TODER''e üye olabilir.', 1),
('Üyelik başvurusu nasıl yapılır?', 'Web sitemizdeki ''Üyelik Başvurusu'' formunu doldurarak ön başvurunuzu iletebilirsiniz. Yönetim kurulumuzun değerlendirmesi sonrasında sizinle iletişime geçilerek gerekli evraklar talep edilecektir.', 2),
('Derneğin amacı nedir?', 'TODER, otopark sektöründeki standartları yükseltmek, üyeler arası dayanışmayı artırmak, sektörel sorunlara ortak çözümler üretmek ve kamu kurumları ile sektör arasında köprü vazifesi görmek amacıyla kurulmuştur.', 3),
('Aidat ödemeleri nasıl yapılır?', 'Üyelik aidatları yıllık olarak tahsil edilmektedir. Ödemeler derneğimizin banka hesaplarına havale/EFT yoluyla veya kredi kartı ile yapılabilmektedir.', 4),
('Komisyonlara nasıl katılabilirim?', 'Üyelerimiz, ilgi ve uzmanlık alanlarına göre Teknoloji, Hukuk, Eğitim veya İletişim komisyonlarında aktif görev alabilirler. Talebinizi yönetim kuruluna iletmeniz yeterlidir.', 5);
